  }
}
convo.Run(suite.container)
  .then((transcript) => {
    debug(convo.header.name + ' ready, calling done function.')
    finish(transcript)
  })
  .catch((err) => {
    debug(convo.header.name + ' failed: ' + util.inspect(err))
    finish(err.transcript, err)
  })
